<?phpclass Miziagui_Fastcash_Block_Form_Deposito extends Mage_Payment_Block_Form {    protected function _construct() {        parent::_construct();        $this->setTemplate('fastcash/form/deposito.phtml');    }        public function getDepAvailableTypes()    {        $_types = Mage::getModel('fastcash/source_methodsdep')->toOptionArray();                $types = array();                $path = 'payment/fastcash_deposito/types';        $availableTypes = explode(',',Mage::getStoreConfig($path) );                foreach ($_types as $data) {            if (isset($data['label']) && isset($data['value'])) {                if(in_array($data['value'], $availableTypes))                    $types[$data['value']] = $data['label'];                            }        }                 return $types;    }}