<?phpclass Miziagui_Fastcash_Block_Form_Transferencia extends Mage_Payment_Block_Form {    protected function _construct() {        parent::_construct();        $this->setTemplate('fastcash/form/transferencia.phtml');    }        public function getTransAvailableTypes()    {        $_types = Mage::getModel('fastcash/source_methodstrans')->toOptionArray();                $types = array();        foreach ($_types as $data) {            if (isset($data['label']) && isset($data['value'])) {                $types[$data['value']] = $data['label'];            }        }        if ($method = $this->getMethod()) {            $availableTypes = $method->getConfigData('types');                        if ($availableTypes) {                $availableTypes = explode(',', $availableTypes);                foreach ($types as $code=>$name) {                    if (!in_array($code, $availableTypes)) {                        unset($types[$code]);                    }                }            }        }        return $types;    }}