<?phpclass Miziagui_Fastcash_Block_Success_Payment extends Mage_Core_Block_Template {    public function _getHelper(){        return Mage::helper('fastcash/payment');            }          public function _construct(){        $this->setTemplate('fastcash/success/payment.phtml');          }        public function loadData($id){        return $this->_getHelper()->loadOrder($id);            }    }