<?phpclass Miziagui_Fastcash_Block_Success_Payment extends Mage_Core_Block_Template {    public function _getHelper(){        return Mage::helper('fastcash/payment');            }              public function getInfo(){        $lastId = Mage::getSingleton('checkout/session')->getLastOrderId();        return $this->_getHelper()->loadOrder($lastId);            }    }