<?phprequire_once Mage::getBaseDir('lib')."/Fastcash/Fastcash.php";class Miziagui_Fastcash_Model_Source_Methodsdep {    public function toOptionArray() {        $methods = Fastcash\PaymentMethodsOptions::$Deposit;        $returnArray = array();        foreach($methods as $key => $item){            $returnArray[] = array('value' => $item, 'label' => $key);        }        return $returnArray;    }}